$((function(){let zipcode=$("#zipcode"),url="https://geo.api.gouv.fr/communes?codePostal=",city=$("#city"),errorMessage=$("#error-message");$(zipcode).on("blur",(function(){let code=$(this).val();$.ajax({type:"GET",url:url+code+"&format=json",success:function(data){$(city).find("option").remove(),data.length?($(errorMessage).text("").hide(),$.each(data,(function(){$(city).append(`<option value="${this.nom}">${this.nom}</option>`)}))):$(zipcode).val()?$(errorMessage).text("Veuillez rentrer un code postal valide").show():$(errorMessage).text("").hide()}})}))}));